        "sourceNameEs": "Aitzina Folk",
        "sourceNameEu": "Aitzina Folk",
        "sourceUrlEs": "https://aitzinafolk.org/evento/ana-alcaide-castilla-la-mancha/",
        "sourceUrlEu": "https://aitzinafolk.org/eu/evento/ana-alcaide-castilla-la-mancha/",
        "priceEs": "15 €",
